var direBonjour = function() { // Création de fonction placé dans une variable.

    console.log('Bonjour !');

}
exports.direBonjour = direBonjour; // Export de la fonction pour qu'elle soit utilisable de l'extérieur.
								   //(Indispensable pour l'utilisation du code en module)

								   
exports.direByeBye = function() { /// Méthode alternative

    console.log('Bye bye !');

}


/// NOTE: Toutes les fonctions que vous n'exportez pas dans votre fichier de module resteront privées.
